mapper.select:
select * from message
where id in (
    select max(id) from message
    where from_id = #{userId}
    or to_id = #{userId}
    and status != 2
    and from_id != 1
    group by conversation_id
)
order by create_time DESC
limit #{offset},#{limit}

select count(id) from message
where id in (
    select max(id) from message
    where from_id = #{userId}
    or to_id = #{userId}
    and status != 2
    and from_id != 1
    group by conversation_id
)

select * from message
where conversation_id = #{conversationId}
and status != 2
and from_id != 1
order by create_time DESC
limit #{offset}, #{limit}

select count(id) from message
where to_id = #{userId}
and status = 0
and from_id != 1

    and conversation_id = #{conversationId}

mapper.update:
update message set status = #{status}
where id in

    #{id}
